-- Create a temporary table\nCREATE GLOBAL TEMPORARY TABLE temp_table (\n    id NUMBER,\n    data VARCHAR2(100)\n) ON COMMIT PRESERVE ROWS;\n\n-- Create an index on the temporary table\nCREATE INDEX temp_table_idx ON temp_table(id);\n\n-- Use the temporary table within your session\nINSERT INTO temp_table (id, data) VALUES (1, 'Example Data');\n-- Your queries here\n\n-- Drop the index if it is no longer needed\nDROP INDEX temp_table_idx;\n\n-- Optionally, drop the temporary table if it won't be reused\nDROP TABLE temp_table;