SELECT \n    id,\n    name,\n    start_date,\n    end_date,\n    CASE WHEN MONTH(start_date) <= 1 AND MONTH(end_date) >= 1 THEN 1 ELSE 0 END AS Jan,\n    CASE WHEN MONTH(start_date) <= 2 AND MONTH(end_date) >= 2 THEN 1 ELSE 0 END AS Feb,\n    CASE WHEN MONTH(start_date) <= 3 AND MONTH(end_date) >= 3 THEN 1 ELSE 0 END AS Mar,\n    CASE WHEN MONTH(start_date) <= 4 AND MONTH(end_date) >= 4 THEN 1 ELSE 0 END AS Apr,\n    CASE WHEN MONTH(start_date) <= 5 AND MONTH(end_date) >= 5 THEN 1 ELSE 0 END AS May,\n    CASE WHEN MONTH(start_date) <= 6 AND MONTH(end_date) >= 6 THEN 1 ELSE 0 END AS Jun,\n    CASE WHEN MONTH(start_date) <= 7 AND MONTH(end_date) >= 7 THEN 1 ELSE 0 END AS Jul,\n    CASE WHEN MONTH(start_date) <= 8 AND MONTH(end_date) >= 8 THEN 1 ELSE 0 END AS Aug,\n    CASE WHEN MONTH(start_date) <= 9 AND MONTH(end_date) >= 9 THEN 1 ELSE 0 END AS Sep,\n    CASE WHEN MONTH(start_date) <= 10 AND MONTH(end_date) >= 10 THEN 1 ELSE 0 END AS Oct,\n    CASE WHEN MONTH(start_date) <= 11 AND MONTH(end_date) >= 11 THEN 1 ELSE 0 END AS Nov,\n    CASE WHEN MONTH(start_date) <= 12 AND MONTH(end_date) >= 12 THEN 1 ELSE 0 END AS Dec\nFROM \n    your_table_name;