DECLARE @BatchSize INT = 10000; -- Adjust the batch size according to your system's capacity\nWHILE (1 = 1)\nBEGIN\n    BEGIN TRANSACTION;\n    DELETE TOP (@BatchSize)\n    FROM YourTableName;\n    \n    IF @@ROWCOUNT = 0\n    BEGIN\n        COMMIT TRANSACTION;\n        BREAK;\n    END\n    \n    COMMIT TRANSACTION;\n    -- Optionally, add a delay to reduce load on the server\n    -- WAITFOR DELAY '00:00:01';\nEND