CREATE FUNCTION dbo.ConvertUnixTimeToLocalTime(@unix_time INT)\nRETURNS DATETIME\nAS\nBEGIN\n    DECLARE @dateTime DATETIME;\n    DECLARE @isDST BIT;\n    \n    -- Convert Unix time to UTC datetime\n    SET @dateTime = DATEADD(SECOND, @unix_time, '1970-01-01 00:00:00');\n    \n    -- Determine if the date falls within daylight saving time\n    SET @isDST = CASE\n        WHEN @dateTime >= 'YYYY-MM-DD hh:mm:ss' AND @dateTime < 'YYYY-MM-DD hh:mm:ss' THEN 1\n        ELSE 0\n    END;\n    \n    -- Adjust for GMT+1 and daylight saving time\n    SET @dateTime = DATEADD(HOUR, 1 + @isDST, @dateTime);\n    \n    RETURN @dateTime;\nEND;