DECLARE\n    l_sql_tune_task_id VARCHAR2(100);\n  BEGIN\n    l_sql_tune_task_id := DBMS_SQLTUNE.create_tuning_task(\n                           sql_text    => 'YOUR QUERY HERE',\n                           user_name   => 'YOUR_USER',\n                           scope       => DBMS_SQLTUNE.scope_comprehensive,\n                           time_limit  => 60,\n                           task_name   => 'YOUR_TUNING_TASK',\n                           description => 'Tuning task for your query');\n    DBMS_SQLTUNE.execute_tuning_task(task_name => 'YOUR_TUNING_TASK');\n  END;\n  /