DECLARE\n  v_start_time TIMESTAMP;\n  v_end_time TIMESTAMP;\n  v_elapsed_time INTERVAL DAY TO SECOND;\n  v_input_data CLOB;\n  v_output_data RAW(20); -- SHA-1 produces a 20-byte hash\nBEGIN\n  -- Prepare a CLOB of desired size (e.g., 4 MB to 30 MB)\n  DBMS_LOB.CREATETEMPORARY(v_input_data, TRUE);\n  FOR i IN 1 .. 4 LOOP -- Adjust the loop based on desired size\n    DBMS_LOB.WRITEAPPEND(v_input_data, 1048576, RPAD('A', 1048576, 'A')); -- 1 MB chunks\n  END LOOP;\n\n  -- Measure the time taken to compute the SHA-1 hash\n  v_start_time := SYSTIMESTAMP;\n  v_output_data := DBMS_CRYPTO.HASH(UTL_RAW.CAST_TO_RAW(v_input_data), DBMS_CRYPTO.HASH_SH1);\n  v_end_time := SYSTIMESTAMP;\n\n  -- Calculate elapsed time\n  v_elapsed_time := v_end_time - v_start_time;\n  \n  -- Output the elapsed time\n  DBMS_OUTPUT.PUT_LINE('Elapsed time for hashing: ' || v_elapsed_time);\n  \n  -- Clean up\n  DBMS_LOB.FREETEMPORARY(v_input_data);\nEND;\n/