CREATE FUNCTION dbo.YourFunctionWithID (@UID UNIQUEIDENTIFIER)\nRETURNS @ResultTable TABLE\n(\n    Column1 INT,\n    Column2 NVARCHAR(50),\n    -- Other columns\n    FakeUniqueIdentifier UNIQUEIDENTIFIER\n)\nAS\nBEGIN\n    INSERT INTO @ResultTable (Column1, Column2, FakeUniqueIdentifier)\n    SELECT \n        Column1,\n        Column2,\n        @UID AS FakeUniqueIdentifier\n    FROM \n        YourTable;\n    \n    RETURN;\nEND;\nGO\n\n-- Call the function with NEWID()\nDECLARE @UID UNIQUEIDENTIFIER = NEWID();\nSELECT * FROM dbo.YourFunctionWithID(@UID);