DECLARE @sql NVARCHAR(MAX);\n\n-- Initialize the SQL variable\nSET @sql = N'SELECT Name, \n             STUFF((SELECT '', '' + col\n                    FROM (VALUES (''OPTION1'', OPTION1), (''OPTION2'', OPTION2), \n                                 (''OPTION3'', OPTION3), (''OPTION4'', OPTION4), \n                                 (''OPTION5'', OPTION5)) AS v(col, val)\n                    WHERE val = 1\n                    FOR XML PATH(''''), TYPE).value(''.'', ''NVARCHAR(MAX)''), 1, 2, '''') AS Options\n             FROM your_table_name_here';\n\n-- Execute the dynamic SQL\nEXEC sp_executesql @sql;