SELECT \n    TextData,\n    ApplicationName,\n    NTUserName,\n    LoginName,\n    SPID,\n    StartTime \nFROM sys.fn_trace_gettable(CONVERT(VARCHAR(256), \n    (SELECT TOP 1 [value] \n     FROM sys.fn_trace_getinfo(NULL) \n     WHERE [property] = 2)), DEFAULT) \nWHERE EventClass IN (46,47,164) -- Filter for specific events like Object:Deleted, Object:Altered\nAND StartTime > DATEADD(day, -1, GETDATE())\nORDER BY StartTime DESC;