DECLARE\n    l_bfile BFILE;\n    l_clob  CLOB;\nBEGIN\n    -- Specify the directory object and file name\n    l_bfile := BFILENAME('YOUR_DIRECTORY_OBJECT', 'badges.xml');\n    DBMS_LOB.CREATETEMPORARY(l_clob, TRUE);\n    DBMS_LOB.FILEOPEN(l_bfile, DBMS_LOB.FILE_READONLY);\n    DBMS_LOB.LOADCLOBFROMFILE(l_clob, l_bfile, DBMS_LOB.GETLENGTH(l_bfile));\n    DBMS_LOB.FILECLOSE(l_bfile);\n\n    -- Insert the XML data into the table\n    INSERT INTO badges_xml (xml_data) VALUES (XMLTYPE(l_clob));\n\n    DBMS_LOB.FREETEMPORARY(l_clob);\nEND;\n/