USE DatabaseA;\nGO\n\nCREATE TRIGGER trg_UpdateTableB\nON TableA\nFOR INSERT, UPDATE, DELETE\nAS\nBEGIN\n    -- Check if there are any rows affected\n    IF EXISTS (SELECT * FROM inserted) OR EXISTS (SELECT * FROM deleted)\n    BEGIN\n        -- Update TableB in DatabaseB\n        UPDATE DatabaseB.dbo.TableB\n        SET ColumnB = 'SomeValue'\n        WHERE SomeCondition = 'ConditionValue';\n\n        -- You can also insert or delete rows in DatabaseB.dbo.TableB if required\n        -- INSERT INTO DatabaseB.dbo.TableB (Column1, Column2) VALUES (Value1, Value2);\n        -- DELETE FROM DatabaseB.dbo.TableB WHERE SomeCondition = 'ConditionValue';\n    END\nEND\nGO