-- Example of generating a custom PK in SQL Server\nDECLARE @ClientID CHAR(4) = 'AB12'; -- Unique Client ID (fixed per client)\nDECLARE @Random CHAR(2) = RIGHT('0' + CAST(ABS(CHECKSUM(NEWID())) % 100 AS VARCHAR(2)), 2); -- Random 2-digit number\n\n-- Generate timestamp-based component (8 characters)\nDECLARE @Timestamp CHAR(8);\nSET @Timestamp = CONVERT(CHAR(8), GETDATE(), 112); -- YYYYMMDD format\n\n-- Combine components to form the custom PK\nDECLARE @CustomPK CHAR(14);\nSET @CustomPK = @Timestamp + @ClientID + @Random;\n\n-- Example Insert\nINSERT INTO YourTable (PrimaryKeyColumn, OtherColumns)\nVALUES (@CustomPK, ...);