-- Lookup table for product codes\nCREATE TABLE ProductCodes (\n    code CHAR(2) PRIMARY KEY,\n    name VARCHAR(100),\n    CONSTRAINT chk_code CHECK (code IN ('FB', 'AB', 'CD'))\n);\n\n-- Main table referencing the lookup table\nCREATE TABLE Products (\n    product_code CHAR(2),\n    product_name VARCHAR(100),\n    PRIMARY KEY (product_code),\n    FOREIGN KEY (product_code) REFERENCES ProductCodes(code)\n);