DECLARE\n  CURSOR cur_source_data IS\n    SELECT * FROM source_table; -- Replace with your actual source table\n  v_row source_table%ROWTYPE;\n  error_log EXCEPTION;\n  PRAGMA EXCEPTION_INIT(error_log, -1); -- Custom exception to catch all errors\nBEGIN\n  FOR v_row IN cur_source_data LOOP\n    BEGIN\n      INSERT INTO target_table (col1, col2, col3) -- Replace with your target table and columns\n      VALUES (v_row.col1, v_row.col2, v_row.col3);\n    EXCEPTION\n      WHEN error_log THEN\n        -- Log the error details\n        INSERT INTO error_log_table (error_message, row_data)\n        VALUES (SQLERRM, v_row.col1 || ', ' || v_row.col2 || ', ' || v_row.col3);\n        -- Continue with the next row\n    END;\n  END LOOP;\n  COMMIT;\nEND;