SELECT DISTINCT\n    t1.id, t1.title, t2.create, t2.lastEdit, t2.authorID, t3.username, \n    t4.title AS userTitle, t3.permission, t5.create AS commentCreate, \n    t5.authorID AS commentAuthor, t6.username AS commentUsername, \n    t6.permission AS commentPermission\nFROM rantPostit AS t1\nLEFT JOIN rantPostitInfo AS t2 \n    ON t1.id = t2.referencePostitID\nLEFT OUTER JOIN rantUser AS t3 \n    ON t2.authorId = t3.id\nLEFT OUTER JOIN rantUserInfo AS t4 \n    ON t4.referenceUserID = t2.authorId\nLEFT OUTER JOIN (\n    SELECT * FROM rantCommentInfo AS sub_t5\n    WHERE sub_t5.referenceType = 8\n    AND sub_t5.create = (\n        SELECT MAX(sub_sub_t5.create)\n        FROM rantCommentInfo AS sub_sub_t5\n        WHERE sub_sub_t5.referenceType = 8\n        AND sub_sub_t5.referenceID = sub_t5.referenceID\n    )\n) AS t5 \n    ON t5.referenceID = t1.id\nLEFT OUTER JOIN rantUser AS t6 \n    ON t6.id = t5.authorID\nORDER BY t2.create DESC, t5.create DESC;