SELECT \n        a.ID_Asset,\n        c.CategoryName,\n        cust.CustodianName,\n        m.ManufacturerName,\n        s.SignedOutToName\n    FROM \n        Assets a\n    JOIN \n        Categories c ON a.CategoryID = c.CategoryID\n    JOIN \n        Custodians cust ON a.CustodianID = cust.CustodianID\n    JOIN \n        Manufacturers m ON a.ManufacturerID = m.ManufacturerID\n    JOIN \n        SignedOutTo s ON a.SignedOutToID = s.SignedOutToID