-- Create a WorkOrders table with an IDENTITY field\nCREATE TABLE WorkOrders (\n    WorkOrderID INT IDENTITY(1,1) PRIMARY KEY,\n    WorkOrderNumber INT UNIQUE NOT NULL,\n    -- Other columns...\n);\n\n-- Populate WorkOrders table\nINSERT INTO WorkOrders (WorkOrderNumber, ...)\nSELECT \n    ROW_NUMBER() OVER (ORDER BY ExistingOrderNumber) AS WorkOrderNumber,\n    -- Other columns...\nFROM \n    OldWorkOrders;