-- Create a covering index for better performance\nCREATE NONCLUSTERED INDEX IX_Covering_KeywordValueGroups_Name\nON dbo.KeywordValueGroups (name)\nINCLUDE (keywordValueGrpId);\n\n-- Create a covering index for the ClientDefinitionEntry table\nCREATE NONCLUSTERED INDEX IX_Covering_ClientDefinitionEntry\nON dbo.ClientDefinitionEntry (keywordGroupId)\nINCLUDE (processPath);\n\n-- Optimized query\nSELECT TOP 1 cde.processPath AS 'keywordValue', COUNT(*) AS 'total'\nFROM dbo.ClientDefinitionEntry AS cde\nINNER JOIN dbo.KeywordValueGroups AS kvg\nON cde.keywordGroupId = kvg.keywordValueGrpId\nWHERE kvg.[name] = @definitionName\nGROUP BY cde.processPath\nORDER BY total DESC;