SELECT ARTICLE_NO\nFROM AUCTIONS1\nWHERE (STR_TO_DATE(ACCESSSTARTS, '%d/%m/%Y %k:%i:%s'), ARTICLE_NO) >\n      (SELECT STR_TO_DATE(ACCESSSTARTS, '%d/%m/%Y %k:%i:%s'), ARTICLE_NO\n       FROM AUCTIONS1\n       WHERE ARTICLE_NO = $pk)\nORDER BY STR_TO_DATE(ACCESSSTARTS, '%d/%m/%Y %k:%i:%s'), ARTICLE_NO\nLIMIT 1;