CREATE TRIGGER trg_SetDefaultTime\nON YourTableName\nAFTER INSERT\nAS\nBEGIN\n    UPDATE t\n    SET t.TimeColumn = \n        CASE \n            WHEN i.WorkCategory = 'A' THEN 1\n            WHEN i.WorkCategory = 'B' THEN 2\n            -- Add more conditions as needed\n            ELSE t.TimeColumn -- Retain the original value if none of the conditions match\n        END\n    FROM YourTableName t\n    INNER JOIN inserted i ON t.PrimaryKeyColumn = i.PrimaryKeyColumn\n    WHERE i.TimeColumn IS NULL -- Only update if TimeColumn was not provided\nEND