CREATE TABLE YourTable (\n    ID INT PRIMARY KEY,\n    OtherTable1ID INT NULL,\n    OtherTable2ID INT NULL,\n    OtherTable3ID INT NULL,\n    CONSTRAINT CHK_OneColumnOnly CHECK (\n        (CASE WHEN OtherTable1ID IS NOT NULL THEN 1 ELSE 0 END +\n         CASE WHEN OtherTable2ID IS NOT NULL THEN 1 ELSE 0 END +\n         CASE WHEN OtherTable3ID IS NOT NULL THEN 1 ELSE 0 END) = 1\n    ),\n    FOREIGN KEY (OtherTable1ID) REFERENCES OtherTable1(ID),\n    FOREIGN KEY (OtherTable2ID) REFERENCES OtherTable2(ID),\n    FOREIGN KEY (OtherTable3ID) REFERENCES OtherTable3(ID)\n);