CREATE PROCEDURE ForwardDemoDataDates\n    @DaysToAdd INT,\n    @DemoUserAccountId INT\nAS\nBEGIN\n    DECLARE @TableName NVARCHAR(128)\n    DECLARE @ColumnName NVARCHAR(128)\n    DECLARE @SQL NVARCHAR(MAX)\n\n    DECLARE TableCursor CURSOR FOR\n    SELECT table_name \n    FROM Information_Schema.Tables \n    WHERE TABLE_TYPE = 'BASE TABLE'\n    AND table_name NOT LIKE 'Report_%'\n    AND table_name NOT IN ('Accounts', 'Manifest', 'System', 'Users')\n\n    OPEN TableCursor\n    FETCH NEXT FROM TableCursor INTO @TableName\n\n    WHILE @@FETCH_STATUS = 0\n    BEGIN\n        DECLARE ColumnCursor CURSOR FOR\n        SELECT column_name\n        FROM Information_Schema.Columns\n        WHERE table_name = @TableName\n        AND data_type IN ('datetime', 'datetime2')\n\n        OPEN ColumnCursor\n        FETCH NEXT FROM ColumnCursor INTO @ColumnName\n\n        WHILE @@FETCH_STATUS = 0\n        BEGIN\n            SET @SQL = 'UPDATE ' + QUOTENAME(@TableName) + \n                       ' SET ' + QUOTENAME(@ColumnName) + ' = DATEADD(day, @DaysToAdd, ' + QUOTENAME(@ColumnName) + ')' + \n                       ' WHERE UserAccountId = @DemoUserAccountId'\n\n            EXEC sp_executesql @SQL, N'@DaysToAdd INT, @DemoUserAccountId INT', @DaysToAdd, @DemoUserAccountId\n\n            FETCH NEXT FROM ColumnCursor INTO @ColumnName\n        END\n\n        CLOSE ColumnCursor\n        DEALLOCATE ColumnCursor\n\n        FETCH NEXT FROM TableCursor INTO @TableName\n    END\n\n    CLOSE TableCursor\n    DEALLOCATE TableCursor\nEND