ALTER FUNCTION [dbo].[fn_GetActivityLogsArranger]\n(\n    @time AS nvarchar(max)\n)\nRETURNS nvarchar(max)\nAS\nBEGIN\n    DECLARE @Return varchar(30)\n\n    SELECT @Return = CASE \n        WHEN @time >= '15:00' AND @time < '15:30' THEN '15:00-15:30'\n        WHEN @time >= '15:30' AND @time < '16:00' THEN '15:30-16:00'\n        WHEN @time >= '16:00' AND @time < '16:30' THEN '16:00-16:30'\n        WHEN @time >= '16:30' AND @time < '17:00' THEN '16:30-17:00'\n        WHEN @time >= '17:00' AND @time < '17:30' THEN '17:00-17:30'\n        WHEN @time >= '17:30' AND @time < '18:00' THEN '17:30-18:00'\n        ELSE 'Unknown'\n    END\n\n    RETURN @Return\nEND