-- Create Credential\n   USE master;\n   GO\n   CREATE CREDENTIAL myCredential WITH IDENTITY = 'Domain\User', \n       SECRET = 'password';\n   GO\n\n   -- Create Proxy\n   EXEC msdb.dbo.sp_add_proxy \n       @proxy_name = N'MyProxy', \n       @credential_name = N'myCredential';\n   GO\n\n   -- Grant the proxy access to the required subsystem\n   EXEC msdb.dbo.sp_grant_proxy_to_subsystem \n       @proxy_name = N'MyProxy', \n       @subsystem_id = 3; -- 3 represents the T-SQL subsystem\n   GO