CREATE PROCEDURE DeleteRecord\n    @TableName NVARCHAR(50),\n    @RecordID INT,\n    @UserID UNIQUEIDENTIFIER\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    DECLARE @SQL NVARCHAR(MAX);\n    SET @SQL = 'DELETE FROM ' + @TableName + ' WHERE ID = ' + CAST(@RecordID AS NVARCHAR(10));\n\n    EXEC sp_executesql @SQL;\n\n    INSERT INTO AuditLog (TableName, Operation, DeletedID, UserID)\n    VALUES (@TableName, 'DELETE', @RecordID, @UserID);\nEND;