-- Set the number of rows to delete per batch\nDECLARE @BatchSize INT = 10000;\n\n-- Loop until no more rows to delete\nWHILE (1 = 1)\nBEGIN\n    -- Delete top N rows\n    DELETE TOP (@BatchSize)\n    FROM YourTable\n    WHERE <condition to identify rows to delete>;\n\n    -- Check if any rows were deleted\n    IF @@ROWCOUNT = 0\n        BREAK;\n\n    -- Optionally, include a delay to reduce the load on the server\n    -- WAITFOR DELAY '00:00:10'; -- Wait for 10 seconds between batches\nEND