-- Begin transaction\nSTART TRANSACTION;\n\n-- Step 1: Set a temporary value to avoid unique constraint violation\nUPDATE PREFERENCES\nSET fk_idstructure = -fk_idstructure\nWHERE fk_idask = 1;\n\n-- Step 2: Swap the fk_idstructure values\nUPDATE PREFERENCES\nSET fk_idstructure = CASE\n    WHEN fk_idstructure = -1 THEN 2\n    WHEN fk_idstructure = -2 THEN 1\n    ELSE fk_idstructure\nEND\nWHERE fk_idask = 1;\n\n-- Commit transaction\nCOMMIT;