BEGIN TRY\n    -- Example of converting and inserting data\n    DECLARE @sourceValue NVARCHAR(255) = 'xze';\n    DECLARE @convertedValue INT;\n\n    -- Attempt to convert the value\n    SET @convertedValue = CAST(@sourceValue AS INT);\n\n    -- If successful, insert into the target table\n    INSERT INTO TargetTable (ColumnName)\n    VALUES (@convertedValue);\n\nEND TRY\nBEGIN CATCH\n    -- If an error occurs, log the error and insert default value\n    INSERT INTO ErrorLog (ErrorMessage, ErrorValue)\n    VALUES (ERROR_MESSAGE(), @sourceValue);\n\n    -- Insert the default value into the target table\n    INSERT INTO TargetTable (ColumnName)\n    VALUES (-1);\nEND CATCH;