CREATE VIEW InterpolatedYieldCurve AS\nWITH BaseData AS (\n    SELECT Year, Yield\n    FROM YieldCurveTable\n),\nInterpolatedData AS (\n    SELECT \n        4 AS Year,\n        0.5 * (SELECT Yield FROM BaseData WHERE Year = 3) + \n        0.5 * (SELECT Yield FROM BaseData WHERE Year = 5) AS Yield\n    UNION ALL\n    SELECT \n        6 AS Year,\n        0.5 * (SELECT Yield FROM BaseData WHERE Year = 5) + \n        0.5 * (SELECT Yield FROM BaseData WHERE Year = 7) AS Yield\n    UNION ALL\n    SELECT \n        8 AS Year,\n        0.5 * (SELECT Yield FROM BaseData WHERE Year = 7) + \n        0.5 * (SELECT Yield FROM BaseData WHERE Year = 10) AS Yield\n    UNION ALL\n    SELECT \n        9 AS Year,\n        0.5 * (SELECT Yield FROM BaseData WHERE Year = 8) + \n        0.5 * (SELECT Yield FROM BaseData WHERE Year = 10) AS Yield\n    -- Add more UNION ALL for other interpolations as needed\n)\nSELECT Year, Yield\nFROM BaseData\nUNION ALL\nSELECT Year, Yield\nFROM InterpolatedData\nORDER BY Year;