CREATE PROCEDURE GetOrders\nAS\nBEGIN\n    BEGIN TRY\n        SELECT \n            [order], \n            CONVERT(DATETIME, orderDate) AS ConvertedOrderDate\n        FROM orders;\n    END TRY\n    BEGIN CATCH\n        -- Handle the error\n        SELECT \n            ERROR_MESSAGE() AS ErrorMessage,\n            ERROR_SEVERITY() AS ErrorSeverity,\n            ERROR_STATE() AS ErrorState;\n    END CATCH\nEND;