CREATE PROCEDURE GetXmlWithDeclaration\nAS\nBEGIN\n    DECLARE @XMLContent NVARCHAR(MAX)\n    DECLARE @XMLDeclaration NVARCHAR(MAX)\n\n    SET @XMLDeclaration = '<?xml version="1.0" encoding="UTF-8"?>'\n\n    SELECT @XMLContent = (SELECT column1, column2 \n                          FROM YourTable \n                          FOR XML AUTO, ELEMENTS)\n\n    PRINT @XMLDeclaration + @XMLContent\nEND\nGO