CREATE TABLE Events (\n    event_id INT AUTO_INCREMENT PRIMARY KEY,\n    event_name VARCHAR(255),\n    event_date DATE,\n    -- other event details\n);\n\nCREATE TABLE EventRepeatInfo (\n    repeat_info_id INT AUTO_INCREMENT PRIMARY KEY,\n    event_id INT,\n    repeat_type ENUM('day', 'week', 'month'),\n    repeat_day INT,   -- for 'day' type\n    repeat_interval INT,  -- for 'every n days' or 'every n weeks'\n    repeat_weekdays SET('Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'), -- for 'week' type\n    repeat_monthdays SET(1, 2, 3, ..., 31), -- for 'month' type\n    FOREIGN KEY (event_id) REFERENCES Events(event_id)\n);