SELECT TOP(6)\n    company.name,\n    COALESCE(french.description, english.description) AS description,\n    company.address\nFROM\n    company\nLEFT JOIN\n    company_descriptions AS french\n    ON company.id = french.company_id\n    AND french.langid = 2\nLEFT JOIN\n    company_descriptions AS english\n    ON company.id = english.company_id\n    AND english.langid = 1\nORDER BY\n    company.id DESC;