SELECT \n    e.event_id,\n    e.event_name,\n    MAX(CASE WHEN r.repeat_type = 'day' THEN r.repeat_day END) AS repeat_day,\n    MAX(CASE WHEN r.repeat_type = 'week' THEN r.repeat_interval END) AS repeat_interval,\n    MAX(CASE WHEN r.repeat_type = 'week' THEN r.repeat_weekdays END) AS repeat_weekdays,\n    MAX(CASE WHEN r.repeat_type = 'month' THEN r.repeat_monthdays END) AS repeat_monthdays\nFROM Events e\nLEFT JOIN EventRepeatInfo r ON e.event_id = r.event_id\nWHERE e.event_id = 1\nGROUP BY e.event_id, e.event_name;