CREATE PROCEDURE GetCustomerOrders\n    @CustomerID INT\nAS\nBEGIN\n    SET NOCOUNT ON;\n    \n    SELECT \n        o.OrderID,\n        o.OrderDate,\n        o.TotalAmount,\n        c.CustomerName\n    FROM \n        Orders o\n    INNER JOIN \n        Customers c ON o.CustomerID = c.CustomerID\n    WHERE \n        o.CustomerID = @CustomerID\n    ORDER BY \n        o.OrderDate DESC;\nEND;