SELECT \n    u.username,\n    p.name AS person_name,\n    ft.type_name,\n    c.claim_value,\n    cc.confidence\nFROM \n    User u\nJOIN \n    ConfidenceInClaim cc ON u.id = cc.user_id\nJOIN \n    Claim c ON cc.claim_id = c.id\nJOIN \n    Person p ON c.person_id = p.id\nJOIN \n    FactType ft ON c.fact_type_id = ft.id\nWHERE \n    u.id = 1 -- User A\nORDER BY \n    cc.confidence DESC;