SELECT \n    mov_id, \n    mov_title, \n    GROUP_CONCAT(DISTINCT categories.cat_name ORDER BY cat_name) as all_genres, \n    GROUP_CONCAT(DISTINCT cat_id ORDER BY cat_id) as all_genres_ids,  \n    GROUP_CONCAT(DISTINCT case when gen_dominant = 1 then gen_catid else 0 end ORDER BY gen_catid) as dominant_genre_ids, \n    GROUP_CONCAT(DISTINCT actors.act_name ORDER BY act_name) as all_actors, \n    GROUP_CONCAT(DISTINCT actors.act_id ORDER BY act_id) as all_actor_ids, \n    mov_desc, \n    mov_added, \n    mov_thumb, \n    mov_hits, \n    mov_numvotes, \n    mov_totalvote, \n    mov_imdb, \n    mov_release, \n    mov_html, \n    mov_type,\n    mov_buytickets,\n    ep_summary,\n    ep_airdate,\n    ep_id,\n    ep_hits,\n    ep_totalNs,\n    ep_totalRs,\n    mov_rating,\n    mov_rating_reason,\n    mrate_name,\n    dir_id, \n    dir_name\nFROM movies\nLEFT JOIN _genres\n    ON movies.mov_id = _genres.gen_movieid\nLEFT JOIN categories\n    ON _genres.gen_catid = categories.cat_id\nLEFT JOIN _actors\n    ON movies.mov_id = _actors.ac_movid\nLEFT JOIN actors\n    ON _actors.ac_actorid = actors.act_id AND act_famous = 1\nLEFT JOIN directors\n    ON movies.mov_director = directors.dir_id\nLEFT JOIN movie_ratings\n    ON movies.mov_rating = movie_ratings.mrate_id\nLEFT JOIN episodes\n    ON mov_id = ep_showid AND ep_season = 0 AND ep_num = 0\nWHERE mov_id = *MOVIE_ID* AND mov_status = 1 \nGROUP BY mov_id