-- Table to store information about sources\nCREATE TABLE Source (\n    id INT PRIMARY KEY,\n    name VARCHAR(255)\n);\n\n-- Table to store information about historical figures\nCREATE TABLE Person (\n    id INT PRIMARY KEY,\n    name VARCHAR(255)\n);\n\n-- Table to store different types of facts\nCREATE TABLE FactType (\n    id INT PRIMARY KEY,\n    type_name VARCHAR(255)\n);\n\n-- Table to store claims made by sources\nCREATE TABLE Claim (\n    id INT PRIMARY KEY,\n    source_id INT,\n    person_id INT,\n    fact_type_id INT,\n    claim_value VARCHAR(255),\n    FOREIGN KEY (source_id) REFERENCES Source(id),\n    FOREIGN KEY (person_id) REFERENCES Person(id),\n    FOREIGN KEY (fact_type_id) REFERENCES FactType(id)\n);\n\n-- Table to store users\nCREATE TABLE User (\n    id INT PRIMARY KEY,\n    username VARCHAR(255)\n);\n\n-- Table to store user confidence in claims\nCREATE TABLE ConfidenceInClaim (\n    user_id INT,\n    claim_id INT,\n    confidence DECIMAL(5, 2),\n    PRIMARY KEY (user_id, claim_id),\n    FOREIGN KEY (user_id) REFERENCES User(id),\n    FOREIGN KEY (claim_id) REFERENCES Claim(id)\n);