-- Step 1: Populate temporary tables\nSELECT\n    P.Column1,\n    P.Column2,\n    P.Column3,\n    ...\nINTO #P\nFROM\n    <joined tables here>;\n\nSELECT\n    A.ColumnX,\n    A.ColumnY,\n    ...\nINTO #A\nFROM\n    dbo.TableReturningFunc1(@StaticParam1, @StaticParam2) AS A;\n\nSELECT\n    B.ColumnX,\n    B.ColumnY,\n    ...\nINTO #B\nFROM\n    dbo.TableReturningFunc2(@StaticParam1, @StaticParam2) AS B;\n\n-- Step 2: Create necessary indexes on temp tables\nCREATE INDEX idx_Key_A ON #A (Key);\nCREATE INDEX idx_Key_B ON #B (Key);\nCREATE INDEX idx_Key_P ON #P (Key);\n\n-- Step 3: Final query using temp tables\nSELECT\n    P.Column1,\n    P.Column2,\n    P.Column3,\n    ...,\n    (\n        SELECT\n            A.ColumnX,\n            A.ColumnY,\n            ...\n        FROM\n            #A AS A\n        WHERE\n            A.Key = P.Key\n        FOR XML AUTO, TYPE\n    ) AS XmlResultA,\n    (\n        SELECT\n            B.ColumnX,\n            B.ColumnY,\n            ...\n        FROM\n            #B AS B\n        WHERE\n            B.Key = P.Key\n        FOR XML AUTO, TYPE\n    ) AS XmlResultB\nFROM\n    #P AS P\nFOR XML AUTO, ROOT('ROOT');\n\n-- Cleanup temp tables\nDROP TABLE #P;\nDROP TABLE #A;\nDROP TABLE #B;