-- Create a temporary table with the required date range\nWITH date_series AS (\n    SELECT generate_series('2023-05-11'::date, '2023-05-17'::date, '1 day') AS date\n)\nSELECT\n    ds.date,\n    COALESCE(t1.bID, '--') AS bID,\n    COALESCE(t1.sName, '--') AS sName\nFROM\n    date_series ds\nLEFT JOIN\n    table1 t1\nON\n    ds.date = t1.date AND t1.fID = 'P1'\nORDER BY\n    ds.date;