CREATE FUNCTION dbo.INET_ATON (@ip VARCHAR(15))\nRETURNS BIGINT\nAS\nBEGIN\n    DECLARE @octet1 INT, @octet2 INT, @octet3 INT, @octet4 INT\n    DECLARE @result BIGINT\n\n    SELECT \n        @octet1 = CONVERT(INT, PARSENAME(@ip, 4)),\n        @octet2 = CONVERT(INT, PARSENAME(@ip, 3)),\n        @octet3 = CONVERT(INT, PARSENAME(@ip, 2)),\n        @octet4 = CONVERT(INT, PARSENAME(@ip, 1))\n\n    SET @result = (@octet1 * 16777216) + (@octet2 * 65536) + (@octet3 * 256) + @octet4\n\n    RETURN @result\nEND\nGO\n\n-- Example usage\nSELECT dbo.INET_ATON('192.168.0.1') AS IPNumber