UPDATE tblCurrentTransaction AS a\nSET a.Balance = (\n    SELECT IIF(IsNull(SUM(b.AmountCharged) - SUM(b.AmountPaid)), a.Balance, (SUM(b.AmountCharged) - SUM(b.AmountPaid)))\n    FROM tblCurrentTransaction AS b\n    WHERE b.TenantTransactionID <= a.TenantTransactionID\n        AND b.Tenant = a.Tenant\n    GROUP BY b.Tenant\n)\nWHERE EXISTS (\n    SELECT 1\n    FROM tblCurrentTransaction AS b\n    WHERE b.TenantTransactionID <= a.TenantTransactionID\n        AND b.Tenant = a.Tenant\n    GROUP BY b.Tenant\n);