DECLARE @BatchSize INT = 10000;\nDECLARE @MinID INT = (SELECT MIN(ID) FROM VerticalTable);\nDECLARE @MaxID INT = (SELECT MAX(ID) FROM VerticalTable);\nDECLARE @CurrentID INT = @MinID;\n\nWHILE @CurrentID <= @MaxID\nBEGIN\n    INSERT INTO #PivotedData (ID, Type10, Type14, Type25)\n    SELECT \n        ID,\n        MAX(CASE WHEN Type = 10 THEN Value ELSE NULL END) AS Type10,\n        MAX(CASE WHEN Type = 14 THEN Value ELSE NULL END) AS Type14,\n        MAX(CASE WHEN Type = 25 THEN Value ELSE NULL END) AS Type25\n    FROM VerticalTable\n    WHERE ID >= @CurrentID AND ID < @CurrentID + @BatchSize\n    GROUP BY ID;\n    \n    SET @CurrentID = @CurrentID + @BatchSize;\nEND;\n\n-- Select data from the temporary table\nSELECT * FROM #PivotedData;