-- Create a user for SELECT queries\nCREATE USER select_user WITH PASSWORD 'select_password';\nGRANT SELECT ON ALL TABLES IN SCHEMA public TO select_user;\n\n-- Create a user for UPDATE and DELETE queries\nCREATE USER modify_user WITH PASSWORD 'modify_password';\nGRANT UPDATE, DELETE ON ALL TABLES IN SCHEMA public TO modify_user;\n\n-- Ensure to REVOKE any unnecessary permissions\nREVOKE ALL ON ALL TABLES IN SCHEMA public FROM public;