CREATE PARTITION FUNCTION [RangePartitionFunction] (DATETIME)\n     AS RANGE RIGHT FOR VALUES ('2021-01-01', '2022-01-01', '2023-01-01');\n\n     CREATE PARTITION SCHEME [RangePartitionScheme]\n     AS PARTITION [RangePartitionFunction] TO ([PRIMARY], [PRIMARY], [PRIMARY], [PRIMARY]);\n\n     CREATE TABLE [dbo].[CacheTable]\n     (\n         [cache_id] INT NOT NULL,\n         [cache_name] NVARCHAR(256) NOT NULL,\n         [cache_event_id] INT NOT NULL,\n         [cache_creation_date] DATETIME NOT NULL,\n         [cache_data] VARBINARY(MAX) NULL\n     )\n     ON [RangePartitionScheme]([cache_creation_date]);