-- User-Defined Variable with Session Scope\nSET @sessionVar = 1;\n\n-- Local Variable with Block Scope\nDELIMITER //\nCREATE PROCEDURE exampleProcedure()\nBEGIN\n    DECLARE blockVar INT DEFAULT 2;\n    SELECT @sessionVar, blockVar; -- Returns 1, 2\nEND //\nDELIMITER ;\n\n-- Call the procedure\nCALL exampleProcedure();\n\n-- Accessing user-defined variable\nSELECT @sessionVar; -- Returns 1\n\n-- Trying to access local variable outside its block will result in an error\n-- SELECT blockVar; -- This would raise an error because blockVar is out of scope