-- Create table with a JSONB column for rules\nCREATE TABLE RULES (\n    id SERIAL PRIMARY KEY,\n    rule JSONB\n);\n\n-- Insert rules as JSONB\nINSERT INTO RULES (rule)\nVALUES \n    ('{"A": 50}'),\n    ('{"A": 51, "B": "xyz"}'),\n    ('{"A": 51, "C": 123}'),\n    ('{"B": "xyz", "C": 456}');\n\n-- Query using JSONB containment\nSELECT * FROM RULES\nWHERE rule @> '{"A": 50}'::jsonb\n   OR rule @> '{"B": "xyz"}'::jsonb\n   OR rule @> '{"C": 456}'::jsonb;