CREATE PROC spBaz\n(\n  @fooInt INT = 0,\n  @fooString VARCHAR(10) = NULL,\n  @barInt INT = 0,\n  @barString VARCHAR(10) = NULL\n)\nAS\nBEGIN\n  -- Validate @fooInt and @fooString\n  IF (@fooInt = 0 AND (@fooString IS NULL OR @fooString = ''))\n  BEGIN\n    THROW 50000, 'Invalid parameter: foo', 1;\n  END\n\n  -- Validate @barInt and @barString\n  IF (@barInt = 0 AND (@barString IS NULL OR @barString = ''))\n  BEGIN\n    THROW 50001, 'Invalid parameter: bar', 1;\n  END\n\n  PRINT 'Validation succeeded';\n  -- Do some work here\n\nEND