CREATE TABLE Essays (\n    essay_id INT PRIMARY KEY,\n    title VARCHAR(255),\n    content TEXT,\n    author_id INT,\n    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP\n);\n\nCREATE TABLE Rebuttals (\n    rebuttal_id INT PRIMARY KEY,\n    essay_id INT,\n    user_id INT,\n    content TEXT,\n    word_count INT,\n    response_number TINYINT, -- 1 for the first response, 2 for the second\n    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n    FOREIGN KEY (essay_id) REFERENCES Essays(essay_id),\n    CHECK (word_count <= 2000 AND response_number = 1 OR word_count <= 500 AND response_number = 2),\n    UNIQUE (essay_id, response_number) -- Enforces only one rebuttal per response number\n);\n\nCREATE TABLE Comments (\n    comment_id INT PRIMARY KEY,\n    essay_id INT,\n    user_id INT,\n    content VARCHAR(1000), -- Fixed length of n chars\n    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n    FOREIGN KEY (essay_id) REFERENCES Essays(essay_id)\n);