CREATE OR REPLACE PROCEDURE generate_insert_statements(p_a_id IN NUMBER) IS\n  v_a_id number := p_a_id;\n  v_name varchar2(100);\n  v_b_id number;\n  \n  CURSOR c_b (c_a_id number) IS\n    SELECT b_id, a_id FROM b WHERE a_id = c_a_id;\n  \n  PROCEDURE insert_a (p_a_id number) IS\n  BEGIN\n    SELECT name INTO v_name FROM a WHERE a_id = p_a_id;\n    dbms_output.put_line('INSERT INTO a(a_id, name) VALUES (' || p_a_id || ', ''' || v_name || ''');');\n  END insert_a;\n  \n  PROCEDURE insert_b (p_b_id number, p_a_id number) IS\n  BEGIN\n    dbms_output.put_line('INSERT INTO b(b_id, a_id) VALUES (' || p_b_id || ', ' || p_a_id || ');');\n  END insert_b;\n  \nBEGIN\n  -- Insert statement for the root row in table A\n  insert_a(v_a_id);\n  \n  -- Insert statements for all dependent rows in table B\n  FOR r_b IN c_b(v_a_id) LOOP\n    insert_b(r_b.b_id, r_b.a_id);\n  END LOOP;\nEND generate_insert_statements;\n/